package bd.edu.seu;import java.util.ArrayList;import java.util.Collections;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        int n = input.nextInt();        long[][] customer_order = new long[n][2];        for (int i = 0; i < n; i++) {            for (int j = 0; j < 2; j++) {                customer_order[i][j] = input.nextLong();            }        }        findCustomerSequence(customer_order, n);    }    private static void findCustomerSequence(long[][] customer_order, int n) {        List<Long> sequence = new ArrayList<>();        List<Long> sortedSequence = new ArrayList<>();        for (int i = 0; i < n; i++) {            sequence.add(customer_order[i][0] + customer_order[i][1]);            sortedSequence.add(customer_order[i][0] + customer_order[i][1]);        }        Collections.sort(sortedSequence);        for(int i=0;i<sortedSequence.size();i++){            for(int j=0;j<sequence.size();j++){                if(sequence.get(j).equals(sortedSequence.get(i))){                    System.out.print((j+1)+" ");                    sequence.set(j,-1L);                    break;                }            }        }    }}