package bd.edu.seu;import java.util.LinkedList;import java.util.Scanner;public class Main {    static String organizingContainers(int[][] container) {        LinkedList<Integer> ball = new LinkedList<>();        LinkedList<Integer> container1 = new LinkedList<>();        for(int i=0;i<container.length;i++){            int rowSum=0;            int colSum=0;            for(int j=0;j<container.length;j++){                rowSum+=container[i][j];                colSum+=container[j][i];            }            ball.add(rowSum);            container1.add(colSum);        }        container1.removeAll(ball);        if(!container1.isEmpty())return "Impossible";        return "Possible";    }    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        int q = input.nextInt();        while(q-->0){            int n = input.nextInt();            int[][] container=new int[n][n];            for(int i=0;i<n;i++){                for(int j=0;j<n;j++){                    container[i][j]=input.nextInt();                }            }            System.out.println(organizingContainers(container));        }    }}